    <div class=\"container\">
        <h1 class=\"text-center mt-5 mb-5\">Partager un souvenir</h1>
    </div>

    <div class=\"container\">
        {{ form_start(form) }}
        <div class=\"my-4\">{{ form_widget(form.title) }}</div>
        <div class=\"mb-4\">Votre souvenir : {{ form_widget(form.content) }}</div>
        <div class=\"d-flex justify-content-end\">
            <button class=\"btn btn-success\">{{ button_label|default('Envoyer le souvenir') }}</button>
        </div>
        {{ form_end(form) }}
    </div>

{% endblock %}

