    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %} Login{% endblock %}

{% block body %}

    {% if error %}
        <div class=\"alerte alert-danger\">Une erreur s'est produite lors de la connexion</div>
    {% endif %}

    <div class=\"container\">
        <div class=\"row mt-5\">
            <div class=\"col-md-3\"></div>
            <div class=\"col-md-6\">
                <div class=\"bg-light py-3 px-3\">
                    <h1 class=\"text-center\">Se connecter</h1>


                    <form action=\"{{ path('login') }}\" method=\"POST\">
                        <div class=\"form-group\">
                            <input type=\"text\" name=\"_username\" id=\"email\" class=\"form-control\" placeholder=\"Email\" required value=\"{{ last_username }}\">
                        </div>
                        <div class=\"form-group\">
                            <input type=\"password\" name=\"_password\" id=\"password\" class=\"form-control\" placeholder=\"Password\" required>
                        </div>
                        <div class=\"form-group d-flex justify-content-end\">
                            <button type=\"submit\" class=\"btn btn-success\">Se connecter</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>

{% endblock %}", "security/login.html.twig", "/home/manon/Documents/Projets perso/memories/templates/security/login.html.twig");
